/*
** Funcao que retorna a cor de acordo com a calibracao RGB
** Params: nothing
*/
TLegoColors rightColor()
{
	//Creates variables to store the red, green, and blue values
	long int redValue;
	long int greenValue;
	long int blueValue;



	//Get the value of all three channels of the color sensor
	//and store it in the variables
	getColorRGB(S2, redValue, greenValue, blueValue);
	// se alguma COR for detectada
	if((redValue != 0) && (greenValue != 0) && (blueValue != 0)){
		// BRANCO
		if(redValue >= 40 && greenValue >= 40 && blueValue >= 30){
			return colorWhite;
			}else{
			// VERDE
			if((redValue <= 8) && (greenValue >= 10) && (blueValue <= 8)){
				return colorGreen;
				}else{
				// VERMELHO MALDITO
				if((redValue >= 15) && (greenValue <= 12) && (blueValue <= 12)){
					return colorRed;
					}else{
					// AMARELO
					if((redValue >= 30) && (greenValue >= 15) && (blueValue <= 8)){
						return colorYellow;
						}else{
						// AZUL
						if((redValue <= 10) && (greenValue >= 12) && (blueValue >= 12)){
							return colorBlue;
							}else{
							// PRETO
							if((redValue <= 5) && (greenValue <= 5) && (blueValue <= 5)){
								return colorBlack;
							}
						}
					}
				}
			}
		}
	}
	return colorNone;

}

task main(){
	TLegoColors cor = rightColor();
}
